import {endpointSaga, takeLatest} from '@oracle-cx-commerce/store/utils';

import {all} from 'redux-saga/effects';
// all actions exported as part of accessRole stories developed.
export default {
  *saga() {
    yield all([
      takeLatest('createRole', endpointSaga),
      takeLatest('listRoles', endpointSaga),
      takeLatest('getRole', endpointSaga),
      takeLatest('updateRole', endpointSaga),
      takeLatest('listAccessRights', endpointSaga)
    ]);
  }
};
