import {endpointSaga, takeLeading} from '@oracle-cx-commerce/store/utils';
import {all} from 'redux-saga/effects';

export default {
  *saga() {
    yield all([
      takeLeading('addGiftWithPurchaseItemToCart', endpointSaga),
      takeLeading('changeGiftWithPurchaseItemInCart', endpointSaga),
      takeLeading('removeGiftWithPurchasePromotionFromCart', endpointSaga),
      takeLeading('removeAllGiftWithPurchasePromotionsFromCart', endpointSaga),
      takeLeading('getGiftWithPurchaseItemChoicesForCart', endpointSaga)
    ]);
  }
};
