import {endpointSaga, takeEvery, takeLeading} from '@oracle-cx-commerce/store/utils';
import {all} from 'redux-saga/effects';

export default {
  *saga() {
    yield all([
      takeEvery('addShippingGroupsToCart', endpointSaga),
      takeEvery('deleteShippingGroupFromCart', endpointSaga),
      takeLeading('deleteShippingGroupsFromCart', endpointSaga),
      takeEvery('getCartShippingGroup', endpointSaga),
      takeEvery('getCartShippingGroups', endpointSaga),
      takeEvery('relateShippingGroupsToCartItems', endpointSaga),
      takeEvery('updateCartShippingGroup', endpointSaga),
      takeLeading('updateCartShippingGroups', endpointSaga)
    ]);
  }
};
