export const deleteShippingGroupFromCart = {
  packageId: '@oracle-cx-commerce/actions',
  endpoints: ['deleteShippingGroupFromCart'],
  input: '@oracle-cx-commerce/actions/cart-shipping-group/schema/delete-shipping-group-from-cart/input.json',
  description:
    "Delete Shipping Group. Delete Shipping group from an order. Shipping group can be deleted from the order only if no relationship exists for that shippinggroup. Optionally takes the x-ccasset-language headers to get translated content in another language. It has an optional X-CCVisitorId header param which will be used in case of anonumous user. Also it can have optional X-CCOrganization header which is a JSON field to specify current Organization context of logged in user.This endpoint returns the order details irrespective of organization ID passed in request header. If user doesn't belong to order's organization, then the user has no access to delete items from order."
};

