import {endpointSaga, takeLatest} from '@oracle-cx-commerce/store/utils';
import {all} from 'redux-saga/effects';

export default {
  *saga() {
    yield all([
      takeLatest('getCartItemCustomProperties', endpointSaga),
      takeLatest('getContactInfoCustomProperties', endpointSaga),
      takeLatest('getOrderCustomProperties', endpointSaga),
      takeLatest('getProductCustomProperties', endpointSaga),
      takeLatest('getUserCustomProperties', endpointSaga),
      takeLatest('getCategoryCustomProperties', endpointSaga)
    ]);
  }
};
