import {endpointSaga, takeLatest} from '@oracle-cx-commerce/store/utils';
import {all} from 'redux-saga/effects';

export default {
  *saga() {
    yield all([
      takeLatest('getCancelReasons', endpointSaga),
      takeLatest('getSamlSettings', endpointSaga),
      takeLatest('getProductionExperiments', endpointSaga),
      takeLatest('getProductionFacebook', endpointSaga),
      takeLatest('getProductionRecommendations', endpointSaga),
      takeLatest('getCloudConfiguration', endpointSaga)
    ]);
  }
};
