import {endpointSaga, takeLeading} from '@oracle-cx-commerce/store/utils';

import {all} from 'redux-saga/effects';

export default {
  *saga() {
    yield all([
      takeLeading('approveOrder', endpointSaga),
      takeLeading('checkOrderRequiresApproval', endpointSaga),
      takeLeading('rejectOrder', endpointSaga)
    ]);
  }
};
