import {endpointSaga, takeLatest, takeLeading} from '@oracle-cx-commerce/store/utils';
import {all} from 'redux-saga/effects';

export default {
  *saga() {
    yield all([
      takeLatest('listOrganizations', endpointSaga),
      takeLeading('updateOrganization', endpointSaga),
      takeLatest('getOrganization', endpointSaga)
    ]);
  }
};
