import {endpointSaga, takeLeading, takeEvery} from '@oracle-cx-commerce/store/utils';

import {all} from 'redux-saga/effects';

export default {
  *saga() {
    yield all([
      takeLeading('createOrganizationRegistrationRequest', endpointSaga),
      takeLeading('createContactRegistrationRequest', endpointSaga),
      takeEvery('listContactRegistrationRequests', endpointSaga),
      takeEvery('getContactRegistrationRequest', endpointSaga),
      takeLeading('updateContactRegistrationRequest', endpointSaga),
      takeLeading('approveContactRegistrationRequest', endpointSaga),
      takeLeading('rejectContactRegistrationRequest', endpointSaga),
      takeLeading('createMultipleContactRegistrationRequests', endpointSaga)
    ]);
  }
};
