import {endpointSaga, takeLatest, takeLeading} from '@oracle-cx-commerce/store/utils';

import {all} from 'redux-saga/effects';

export default {
  *saga() {
    yield all([
      takeLeading('createReturnRequest', endpointSaga),
      takeLatest('listReturnRequestsForOrder', endpointSaga),
      takeLatest('getReturnRequest', endpointSaga),
      takeLeading('getReturnReasons', endpointSaga),
      takeLatest('initiateReturn', endpointSaga),
      takeLatest('calculateRefund', endpointSaga),
      takeLeading('validateReturns', endpointSaga)
    ]);
  }
};
