import {combineReducers, createReducer} from '@oracle-cx-commerce/store/utils';

/**
 * An action that clears any store data located under
 * searchRepository.typeaheadSearchResults.
 */

export default {
  reducer: combineReducers({
    searchRepository: combineReducers({typeaheadSearchResults: createReducer({clearTypeahead: () => ({})})})
  })
};
