import {combineReducers, createReducer} from '@oracle-cx-commerce/store/utils';

/**
 * An action that sets the value of clientRepository.context.request.filterMenuVisible to true or false.
 */
export default {
  reducer: combineReducers({
    clientRepository: combineReducers({
      context: combineReducers({
        request: createReducer({
          showFilterMenu: (state, {payload}) => ({...state, filterMenuVisible: payload})
        })
      })
    })
  })
};
