const esbuild = require('esbuild');
const externalize = require('./externalize');

const buildCssPlugins = [externalize()];

async function buildCss(file, options) {
  const {minify = false, format, metafile, bundle = true} = options;

  const result = await esbuild.build({
    entryPoints: [file],
    sourcemap: false,
    bundle,
    write: false,
    outdir: 'out',
    minify,
    format,
    metafile,
    plugins: buildCssPlugins
  });

  return result.outputFiles[0];
}

module.exports = () => ({
  name: 'occ-css-in-js',

  setup(build) {
    const options = build.initialOptions;

    const cache = new Map();

    build.onLoad({filter: /\.css$/}, async args => {
      if (!cache.has(args.path)) {
        const result = await buildCss(args.path, {...options});

        cache.set(args.path, result.text);
      }

      const cssText = cache.get(args.path);

      return {
        contents: `export default ${JSON.stringify(cssText)};`,
        loader: 'js',
        watchFiles: [args.path]
      };
    });
  }
});
