const path = require('path');

function parseOutputOptions(options) {
  const outdir = path.normalize(options.outdir || path.parse(options.outfile).dir);

  const outfile = path.normalize(options.outfile || path.join(options.outdir, path.parse(options.entryPoints[0]).base));

  const outName = path.parse(outfile).name;

  return {
    outName,
    outdir,
    outfile
  };
}

module.exports = {parseOutputOptions};
