const cliHelperMethods = require('../cli-helper-methods');

describe('cliHelperMethods:validateConfiguration', () => {
  test('test server urls have their paths removed as part of validation', () => {
    const options = {
      appServerAdmin: 'http://test-admin.com:9080/occs-admin',
      appServer: 'http://test-store.com:9080/occs-admin'
    };
    cliHelperMethods.validateConfiguration(options, ['appServerAdmin', 'appServer']);
    expect(options.appServerAdmin).toEqual('http://test-admin.com:9080');
    expect(options.appServer).toEqual('http://test-store.com:9080');
  });
});
