const {squeeze} = require('@oracle-cx-commerce/tools-util/strings');

describe('String manipulation helper methods tests', () => {
  test('Check default behavior', () => {
    const longText = '0123456789abcdefghijklmnopqrstuvwxyz';
    expect(squeeze(longText)).toBe('0123456789...qrstuvwxyz');
    expect(squeeze(longText, 5)).toBe('01234...vwxyz');
    expect(squeeze(longText, 18)).toBe('0123456789abcdefghijklmnopqrstuvwxyz');
    expect(squeeze(longText, 19)).toBe('0123456789abcdefghi...');
    expect(squeeze(longText, 36)).toBe('0123456789abcdefghijklmnopqrstuvwxyz');
    expect(squeeze(longText, 40)).toBe('0123456789abcdefghijklmnopqrstuvwxyz');
    expect(squeeze(longText, -1)).toBe('');
    expect(squeeze(longText, 0)).toBe('...');
    expect(squeeze(longText, null)).toBe('');
    expect(squeeze(longText, undefined)).toBe('0123456789...qrstuvwxyz');
    expect(squeeze(longText, 'a')).toBe('');
    expect(squeeze(longText, false)).toBe('');
  });
});
