import {getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';
import {createOas3LocalSwaggerEndpointExampleEndpointFactory} from '../../../../core/wapi/oas-3-local-swagger-endpoint-example-endpoint';

export const processInput = payload => {

  return payload;
};

export const processOutput = async response => {
  const json = await getBodyAsJson(response);

  return response.ok
    ? {
        json
      }
    : populateError(response, json);
};

export default createOas3LocalSwaggerEndpointExampleEndpointFactory('listAddresses', {
  processInput,
  processOutput
});
