import {createEndpointFactory} from '@oracle-cx-commerce/endpoints/utils/endpoint-factory';
import {getEndpointOrigin} from '@oracle-cx-commerce/commerce-utils/selector';

const API_NAME = 'oas3SwaggerEndpointExample';

export const createOas3SwaggerEndpointExampleEndpointFactory = createEndpointFactory({
  apiCatalogPaths: ['/ccstore/v1/metadata-catalog'],

  getApiCatalogOrigin({state}) {
    return getEndpointOrigin(state, API_NAME).catalogOrigin;
  },
  getOrigin({state}) {
    return getEndpointOrigin(state, API_NAME).origin;
  },

  getHeaders() {
    const headers = {};

    return headers;
  }
});
