const {dashCase} = require('@oracle-cx-commerce/tools-util/strings');

function actionIndexExportString(actionName) {
  return `export const ${actionName} = () => import('./${dashCase(actionName)}');\n`;
}
function actionMetaExportString(actionName) {
  return `export {${actionName}} from './${dashCase(actionName)}/meta';\n`;
}

module.exports = {
  actionIndexExportString,
  actionMetaExportString
};
