function testBody(actionName) {
  return `import {createBaselineActionTests} from '@oracle-cx-commerce/test/unit/actions';
import * as exportedActions from '../../index';
import actions from '../index';

const expectedActions = ['${actionName}'];

// Run a standard series of tests to verify basic functionality of our defined actions.
createBaselineActionTests({actions, expectedActions, exportedActions});
`;
}
module.exports = options => {
  const content = testBody(options.actionName);

  return [
    {
      fileName: 'index.spec.js',
      relativePath: '__test__',
      content
    }
  ];
};
