const {processSwaggerInput, listOperationIds} = require('@oracle-cx-commerce/cli/templates/endpoints/endpoint-utils');

/**
 * List all available endpoints from a Swagger API Catalog
 *
 * @param      {Object}   options           The command line options including appDir and other widget specific options
 */
async function listEndpoints(options) {
  const {swaggerApiMapping} = await processSwaggerInput(options.swagger);

  listOperationIds(swaggerApiMapping);
}

module.exports = {listEndpoints};
