const {standardizePluginName} = require('@oracle-cx-commerce/cli/templates/utils');

function inputBody(operationID) {
  return `{
    "$schema": "http://json-schema.org/draft-06/schema#",
    "$id": "${operationID}",
    "title": "${operationID}",
    "description": "Description for ${operationID}.",
    "type": "object",
    "properties": {
    },
    "example": {
    }
}`;
}

module.exports = options => {
  let content = '';

  content = options.swagger ? inputBody(options.operationId) : inputBody(standardizePluginName(options.directoryName));

  return [
    {
      fileName: 'input.json',
      relativePath: 'schema',
      content
    }
  ];
};
