const {dashCase} = require('@oracle-cx-commerce/tools-util/strings');

function fetchersIndexExportString(fetcherName) {
  return `/**
 * References to the application's custom data fetchers.
 */
export {default as ${fetcherName}} from './${dashCase(fetcherName)}';
`;
}
function fetchersMetaExportString(fetcherName) {
  return `/**
 * References to the application's custom data fetchers.
 */
export {default as ${fetcherName}} from './${dashCase(fetcherName)}/meta';
`;
}
function fetchersHooksExportString(hookName, fetcherName) {
  return `/**
 * References to the application's custom data fetcher hooks.
 */
export {default as ${hookName}} from './${dashCase(fetcherName)}/hook';
`;
}

module.exports = {
  fetchersIndexExportString,
  fetchersMetaExportString,
  fetchersHooksExportString
};
