const fetcherComments = `/**
 * Custom Fetcher
 *
 * @param {object} store  The Store
 * @param {object} widgetConfig  The widget configuration values coming from Design Studio
 */
`;

function fetcherBody(endpoint = 'ENDPOINT_PLACEHOLDER_VALUE') {
  return `export default (store, widgetConfig) => {
  return store.endpoint('${endpoint}', {});
};
`;
}

module.exports = options => {
  let content = '';
  content += fetcherComments;
  content += fetcherBody(options.endpoint);

  return [
    {
      fileName: 'index.js',
      relativePath: '',
      content
    }
  ];
};
