function fetcherMetaExport(fetcherName, endpoint = 'ENDPOINT_PLACEHOLDER_VALUE', userName) {
  return `export default {
  name: '${fetcherName}',
  packageId: '',
  description: 'Fetcher description for ${fetcherName}',
  endpoints: ['${endpoint}'],
  author: '${userName}'
};
`;
}

module.exports = options => {
  let content = '';
  content += fetcherMetaExport(options.fetcherName, options.endpoint, options.userName);

  return [
    {
      fileName: 'meta.js',
      relativePath: '',
      content
    }
  ];
};
