const {dashCase} = require('@oracle-cx-commerce/tools-util/strings');

function subscriberIndexExportString(subscriberName) {
  return `/**
 * References to the application's custom subscriber.
 */
export const _${subscriberName} = () => import('./${dashCase(subscriberName)}');
`;
}
function subscriberMetaExportString(subscriberName) {
  return `/**
 * References to the application's custom subscriber.
 */
export {default as _${subscriberName}} from './${dashCase(subscriberName)}/meta';
`;
}

module.exports = {
  subscriberIndexExportString,
  subscriberMetaExportString
};
