const {dashCase} = require('@oracle-cx-commerce/tools-util/strings');

function componentsIndexExportString(widgetName) {
  return `export const ${widgetName} = () => import('./${dashCase(widgetName)}');\n`;
}
function componentsMetaExportString(widgetName) {
  return `export {default as ${widgetName}} from './${dashCase(widgetName)}/meta';\n`;
}

module.exports = {
  componentsIndexExportString,
  componentsMetaExportString
};
