const path = require('path');

function localeContent(locale) {
  const localeTag = locale === 'en' ? '' : `[${locale}]`;

  return `export const includeExtraInfoLabel = '${localeTag}Display extra info${localeTag}';
export const includeExtraInfoHelpText = '${localeTag}If selected, the display will include some extra info.${localeTag}';
`;
}

module.exports = options => {
  const files = [];
  for (const locale of options.localesList) {
    files.push({
      fileName: `${locale}.js`,
      relativePath: path.join('config', 'locales'),
      content: localeContent(locale)
    });
  }

  return files;
};
