function localeContent(options, locale) {
  const localeTag = locale === 'en' ? '' : `[${locale}]`;

  return `export const helloText = '${localeTag}sample component text${localeTag}';
export const labelExtraInfo = '${localeTag}...with some extra info!${localeTag}';
export const labelSampleTranslation = '${localeTag}A Translated Label${localeTag}';
${
  options.template && options.template.toLowerCase() === 'currencyselector'
    ? localeCurrencySelectorContent(localeTag)
    : ''
}`;
}
function localeCurrencySelectorContent(localeTag) {
  return `
/**
 * Example locales for Currency Selector example:
 */
export const labelCurrencies = '${localeTag}Currencies:${localeTag}';
export const labelSelectACurrency = '${localeTag}Select a currency...${localeTag}';
export const labelSelectedCurrency = '${localeTag}Selected Currency:${localeTag}';
`;
}

module.exports = options => {
  const files = [];
  for (const locale of options.localesList) {
    files.push({
      fileName: `${locale}.js`,
      relativePath: 'locales',
      content: localeContent(options, locale)
    });
  }

  return files;
};
