module.exports = options => {
  let content = `# ${options.widgetName} README file\n`;
  if (options.template && options.template.toLowerCase() === 'currencyselector') {
    content += 'This widget is based on the CurrencySelector template\n';
  } else {
    content += 'This widget is based on the base template\n';
  }

  return [
    {
      fileName: 'README.md',
      relativePath: '',
      content
    }
  ];
};
