import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {transformRoles} from '@oracle-cx-commerce/endpoints/utils/transform';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';

export const processInput = payload => {
  //fetching query param from the request payload which will be passed as query header while making REST API call.
  const {query = {}} = payload;

  return {
    query
  };
};

export const processOutput = async response => {
  const json = await getBodyAsJson(response);
  const {items = []} = json;

  return response.ok ? {...transformRoles(items), json: items} : populateError(response, json);
};

export default createEndpoint('listRoles', {
  processInput,
  processOutput
});
