import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {transformRole} from '@oracle-cx-commerce/endpoints/utils/transform';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';

export const processInput = payload => {
  ////roleId is a required property in request payload.
  console.assert(payload.roleId !== undefined, 'updateRole requires a roleId.');
  //other details will be passed as body for the PUT REST endpoint.
  const {roleId, ...rest} = payload;

  return {
    params: [roleId],
    body: {...rest}
  };
};

export const processOutput = async response => {
  const json = await getBodyAsJson(response);

  return response.ok ? transformRole(json) : populateError(response, json);
};

export default createEndpoint('updateRole', {
  processInput,
  processOutput
});
