import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';

import {populateError} from '@oracle-cx-commerce/endpoints/utils';

export const processInput = payload => {
  console.assert(
    payload.visitId !== undefined,
    'Missing argument "visitId" for endpoint "saveVisitsAndVisitorsMetric"'
  );
  console.assert(
    payload.visitorId !== undefined,
    'Missing argument "visitorId" for endpoint "saveVisitsAndVisitorsMetric"'
  );

  return {
    body: payload
  };
};
export const processOutput = async response => {
  const json = await getBodyAsJson(response);

  return response.ok ? {json} : populateError(response, json);
};

export default createEndpoint('saveVisitsAndVisitorsMetric', {
  processInput,
  processOutput
});
