import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {transformGiftPurchaseChoicesProducts} from '@oracle-cx-commerce/endpoints/utils/transform';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';

export const processInput = payload => {
  return {
    query: {
      ...payload
    }
  };
};

export const processOutput = async response => {
  const json = await getBodyAsJson(response);

  return response.ok ? transformGiftPurchaseChoicesProducts(json) : populateError(response, json);
};

export default createEndpoint('getGiftWithPurchaseChoices', {
  processInput,
  processOutput
});
