import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {transformOrderCommerceItem} from '@oracle-cx-commerce/endpoints/utils/transform';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';

export const processInput = payload => {
  console.assert(payload.commerceItemId, 'Missing argument "commerceItemId" to get');
  const {queryParams, commerceItemId} = payload;

  return {
    params: [commerceItemId],
    query: {...queryParams}
  };
};

export const processOutput = async (response, state) => {
  const json = await getBodyAsJson(response);

  return response.ok ? transformOrderCommerceItem(json, state) : populateError(response, json);
};

export default createEndpoint('getCartItem', {
  processInput,
  processOutput
});
