import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {transformOrderCommerceItems} from '@oracle-cx-commerce/endpoints/utils/transform';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';

export const processInput = payload => {
  const {queryParams} = payload;

  return {
    query: {...queryParams}
  };
};

export const processOutput = async (response, state) => {
  const json = await getBodyAsJson(response);

  return response.ok ? {...transformOrderCommerceItems(json, state), json} : populateError(response, json);
};

export default createEndpoint('getCartItems', {
  processInput,
  processOutput
});
