import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';
import {transformOrderShippingGroup} from '@oracle-cx-commerce/endpoints/utils/transform';

export const processInput = payload => {
  console.assert(payload.shippingGroupId, 'Missing argument "shippingGroupId" for endpoint "getShippingGroup"');

  const {shippingGroupId} = payload;

  return {
    params: [shippingGroupId]
  };
};

export const processOutput = async (response, state) => {
  const json = await getBodyAsJson(response);

  return response.ok ? transformOrderShippingGroup(json, state) : populateError(response, json);
};

export default createEndpoint('getShippingGroup', {
  processInput,
  processOutput
});
