import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {deleteIncompleteOrder} from '@oracle-cx-commerce/endpoints/utils/transform';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';

export const processInput = payload => {
  console.assert(payload.orderId !== undefined, 'deleteCart requires an orderId.');

  const {orderId, queryParams} = payload;

  return {
    params: [orderId],
    query: {...queryParams}
  };
};

export const processOutput = async (response, state) => {
  const order = await getBodyAsJson(response);

  return response.ok ? deleteIncompleteOrder(order, state) : populateError(response, order);
};

export default createEndpoint('removeProfileIncompleteOrderById', {
  processInput,
  processOutput
});
