import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';

import {populateError} from '@oracle-cx-commerce/endpoints/utils';
import {transformOrdersForListCarts} from '@oracle-cx-commerce/endpoints/utils/transform';

const processInput = ({query, ...rest}) => {
  return {
    query: {
      q: query,
      ...rest,
      sort: 'lastModifiedDate:desc',
      incompleteOnly: true
    }
  };
};

const processOutput = async (response, state) => {
  const json = await getBodyAsJson(response);
  if (!response.ok) {
    return populateError(response, json);
  }
  const itemsObject = transformOrdersForListCarts(json.items, state);

  return {
    orderRepository: {
      orders: {
        ...itemsObject
      }
    },
    json
  };
};
export default createEndpoint('getAllOrdersForProfile', {processInput, processOutput});
