import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {mergeExcludeParams, transformIncompleteOrder} from '@oracle-cx-commerce/endpoints/utils/transform';
import {getCurrentOrder} from '@oracle-cx-commerce/commerce-utils/selector';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';

export const processInput = (payload, state) => {
  const {queryParams, ...payloadBody} = payload;
  const excludeParams = mergeExcludeParams(queryParams, 'getIncompleteOrder');

  const {discountInfo} = getCurrentOrder(state);
  const claimedCouponCodes = Object.keys(discountInfo.orderCouponsMap);
  const unClaimedCouponCodes = Object.keys(discountInfo.unclaimedCouponsMap);
  const newPayload = [...claimedCouponCodes, ...unClaimedCouponCodes].filter(
    value => !payloadBody.coupons.includes(value)
  );

  return {
    body: {coupons: newPayload},
    query: {...queryParams, exclude: excludeParams}
  };
};

export const processOutput = async response => {
  const json = await getBodyAsJson(response);

  return response.ok ? transformIncompleteOrder(json) : populateError(response, json);
};

export default createEndpoint('updateIncompleteOrder', {
  processInput,
  processOutput
});
