import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {populateProductCustomProperties} from '@oracle-cx-commerce/endpoints/utils/transform';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';

export const processInput = payload => {
  const {queryParams} = payload;

  return {
    params: ['product'],
    query: {...queryParams, parent: 'product'}
  };
};

export const processOutput = async response => {
  const json = await getBodyAsJson(response);

  return response.ok ? populateProductCustomProperties(json) : populateError(response, json);
};

export default createEndpoint('getItemType', {
  processInput,
  processOutput
});
