import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {transformCancelReasons} from '@oracle-cx-commerce/endpoints/utils/transform';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';

export const processInput = payload => {
  const {queryParams} = payload;

  return {
    query: {...queryParams}
  };
};

export const processOutput = async response => {
  const json = await getBodyAsJson(response);

  return response.ok ? transformCancelReasons(json.items) : populateError(response, json);
};

export default createEndpoint('getCancelReasons', {
  processInput,
  processOutput
});
