import {createEndpoint} from '@oracle-cx-commerce/endpoints/factory';
import {getBodyAsJson, populateError} from '@oracle-cx-commerce/endpoints/utils';
import {transformProductionRecommendationsResponse} from '@oracle-cx-commerce/endpoints/utils/transform';

export const processInput = payload => {
  const {queryParams} = payload;

  return {
    params: ['production-Recommendations'],
    query: {...queryParams}
  };
};

export const processOutput = async response => {
  const json = await getBodyAsJson(response);

  return response.ok ? transformProductionRecommendationsResponse(json) : populateError(response, json);
};

export default createEndpoint('getExternalServiceData', {
  processInput,
  processOutput
});
