import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {transformGetOrder} from '@oracle-cx-commerce/endpoints/utils/transform';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';

export const processInput = payload => {
  console.assert(payload.orderId !== undefined, 'approveOrder requires an orderId.');
  console.assert(
    payload.ignorePriceWarnings !== undefined,
    'approveOrder requires repriced flag as part of approval or not.'
  );

  return {
    body: payload
  };
};

export const processOutput = async response => {
  const order = await getBodyAsJson(response);

  if (!response.ok) {
    return populateError(response, order);
  }

  return transformGetOrder(order);
};

export default createEndpoint('approveOrder', {
  processInput,
  processOutput
});
