import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {mergeExcludeParams, transformIncompleteOrder} from '@oracle-cx-commerce/endpoints/utils/transform';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';

export const processInput = payload => {
  console.assert(payload.orderId !== undefined, 'copy order endpoint requires order id.');
  const {queryParams, orderId, ...data} = payload;
  const excludeParams = mergeExcludeParams(queryParams, 'copyOrder');

  return {
    query: {...queryParams, exclude: excludeParams},
    body: {
      orderId,
      ...data
    }
  };
};

export const processOutput = async response => {
  const json = await getBodyAsJson(response);

  return response.ok ? transformIncompleteOrder(json) : populateError(response, json);
};

export default createEndpoint('copyOrder', {
  processInput,
  processOutput
});
