import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {mergeExcludeParams, transformGetOrder} from '@oracle-cx-commerce/endpoints/utils/transform';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';

export const processInput = payload => {
  console.assert(payload.orderId !== undefined, 'getOrder requires an orderId.');

  const {orderId, queryParams} = payload;
  const excludeParams = mergeExcludeParams(queryParams, 'getOrder');

  return {
    params: [orderId],
    query: {...queryParams, exclude: excludeParams}
  };
};

export const processOutput = async response => {
  const order = await getBodyAsJson(response);

  return response.ok ? transformGetOrder(order) : populateError(response, order);
};

export default createEndpoint('getOrder', {
  processInput,
  processOutput
});
