import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {transformInitiateCancelOrder} from '@oracle-cx-commerce/endpoints/utils/transform';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';

export const processInput = payload => {
  console.assert(payload.orderId !== undefined, 'initiateCancelOrder requires an orderId.');
  console.assert(payload.cancelReason !== undefined, 'initiateCancelOrder requires a cancelReason.');

  const {orderId, cancelReason} = payload;

  return {
    body: {
      orderId,
      cancelReason
    }
  };
};

export const processOutput = async response => {
  const json = await getBodyAsJson(response);

  if (!response.ok) {
    return populateError(response, json);
  }

  const orderRepository = transformInitiateCancelOrder(json);

  return {...orderRepository, json};
};

export default createEndpoint('initiateCancelOrder', {
  processInput,
  processOutput
});
