import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';
import {transformOrganizationDefaultAddresses} from '@oracle-cx-commerce/endpoints/utils/transform';

export const processInput = payload => {
  console.assert(payload.addressId, 'Missing argument addressId for endpoint deleteAddress');

  const {addressId} = payload;

  return {
    params: [addressId]
  };
};

export const processOutput = async response => {
  const json = await getBodyAsJson(response);

  return response.ok ? transformOrganizationDefaultAddresses(json) : populateError(response, json);
};

export default createEndpoint('deleteAddress', {
  processInput,
  processOutput
});
