import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {populateError} from '@oracle-cx-commerce/endpoints/utils';
import {transformAddress} from '@oracle-cx-commerce/endpoints/utils/transform';

export const processInput = payload => {
  console.assert(payload.addressId, 'Missing argument addressId for endpoint getAddress');

  const {addressId} = payload;

  return {
    params: [addressId]
  };
};

export const processOutput = async response => {
  const json = await getBodyAsJson(response);

  return response.ok
    ? {
        profileRepository: {
          contactInfos: transformAddress(json)
        }
      }
    : populateError(response, json);
};

export default createEndpoint('getAddress', {
  processInput,
  processOutput
});
