import {populateError} from '@oracle-cx-commerce/endpoints/utils';
import {createEndpoint, getBodyAsJson} from '@oracle-cx-commerce/endpoints/factory';
import {transformAddresses} from '@oracle-cx-commerce/endpoints/utils/transform';
import {getCurrentOrganizationId} from '@oracle-cx-commerce/commerce-utils/selector';

export const processInput = ({searchCriteria, ...rest}, state) => {
  const orgId = getCurrentOrganizationId(state);

  return {
    query: {
      orgId,
      q: searchCriteria,
      ...rest
    }
  };
};

export const processOutput = async response => {
  const json = await getBodyAsJson(response);

  return response.ok
    ? {
        profileRepository: {
          contactInfos: transformAddresses(json)
        },
        json
      }
    : populateError(response, json);
};

export default createEndpoint('listAddresses', {
  processInput,
  processOutput
});
